import { useState } from 'react'
import React from 'react'


 export const CartContext = React.createContext()

 export const CartProvider = ({children}) => {
     const [productCartList, setProductCartList] = useState([])

    //  const IsInCart = (id) => setProductCartList(cart.filter(product => product.id !== id))


     const addItem = (item, quantity) => {
         console.log("item", item, "quantity",quantity)
         const newProduct ={
            name,
            item,
            quantity 
        }
         console.log("newProduct", newProduct)
         const newArreglo=[...productCartList]
         newArreglo.push(newProduct)
         setProductCartList(newArreglo)
     }

     const removeItem=(itemId)=>{
     console.log("itemId", itemId)
     const newArreglo=productCartList.filter(product=>product.id !== itemId)
     setProductCartList(newArreglo)
     }

     const clear= () => {
        setProductCartList([])
     }
     return (
         <CartContext.Provider value={{productCartList, addItem, removeItem, clear}}>
             {children }
         </CartContext.Provider>
     )
 }
